import { Component, OnInit } from '@angular/core';
import {Router} from '@angular/router';
import { DomSanitizer, SafeResourceUrl } from '@angular/platform-browser';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  Question_Image_Data :string;
  Header_Image_Data :string;
  Question_Image:SafeResourceUrl;
  Header_Image:SafeResourceUrl;
  constructor(private router: Router,public sanitizer: DomSanitizer) {
    this.Question_Image_Data = 'data:image/jpeg;base64,';
    this.Question_Image_Data += '';
    this.Question_Image_Data += '';
    this.Question_Image_Data += '';
    this.Question_Image_Data += '';
    this.Question_Image_Data += '';
    this.Header_Image_Data = 'data:image/svg+xml;base64,';
  }

  ngOnInit() {
    this.Header_Image = this.sanitizer.bypassSecurityTrustResourceUrl(this.Header_Image_Data);
    this.Question_Image = this.sanitizer.bypassSecurityTrustResourceUrl(this.Question_Image_Data);
  }

  startQuiz() {
      this.router.navigateByUrl('/questions');
  }
}
